function list(response) {
	console.log("Fetch all employees");
	writeToResponse(response, "Fetch all employees");
}

function deleteEmp(response) {
	console.log("Delete employee");
	writeToResponse(response, "Delete employee");
}

function edit(response) {
	console.log("Edit employee details");
	writeToResponse(response, "Edit employee details");
}

function add(response) {
	console.log("Add employee");
	writeToResponse(response, "Add employee");
}

function writeToResponse(response, content) {
	response.writeHead(200, {'Content-Type' : 'text/plain'});
	response.write(content);
	response.end();
}



exports.add = add;
exports.deleteEmp = deleteEmp;
exports.edit = edit;
exports.list = list;